var ProcessorInterface = require('../src/Processor');
var lwip = require('lwip');
var Image = require('./Image');

module.exports = class Processor extends ProcessorInterface {
	open(src) {
		return new Promise(function (resolve, reject) {
			lwip.open(src, function (err, image) {
				if (err) {
					return reject(err);
				}
				resolve(new Image(image));
			})
		});
	}
}