module.exports = class Box {
	constructor(width, height) {
		this._width = width;
		this._height = height;
	}

	getWidth() {
		return this._width;
	}

	getHeight() {
		return this._height;
	}

	scale(ratio) {
		return new Box(this._width * ratio, this._height * ratio);
	}

	widen(width) {
		return new Box(width, width * this._height / this._width);
	}

	heighten(height) {
		return new Box(height * this._width / this._height, height);
	}

	increase(px) {
		return new Box(this._width + px, this._height + px);
	}

	decrease(px) {
		return this.increase(-px);
	}
}