var Box = require('./Box');
var Point = require('./Point');

module.exports = class Zone {
	constructor(startPoint, endPointOrSize) {
		this._startPoint = startPoint;

		if (endPointOrSize instanceof Point) {
			this._endPoint = endPointOrSize;
			this._size = new Box(
				this._endPoint.getX() - this._startPoint.getX(),
				this._endPoint.getY() - this._startPoint.getY()
			);
		} else {
			this._size = endPointOrSize;
			this._endPoint = new Point(
				this._startPoint.getX() + this._size.getWidth(),
				this._startPoint.getY() + this._size.getHeight()
			);
		}
	}

	getStartPoint() {
		return this._startPoint;
	}

	getEndPoint() {
		return this._endPoint;
	}

	getSize() {
		return this._size;
	}
}